package com.resideo.filetransferfrompanel;

import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.Mockito;
import org.robolectric.RobolectricTestRunner;


@RunWith(RobolectricTestRunner.class)
public class Base64ToFileConverterTest {

    @Test
    public void testSaveBase64ToFile_shouldInvokeOnSuccess() {
        String message = "";
        Base64ToFileConverter base64ToFileConverter = new Base64ToFileConverter();
        CompletionHandler completionHandler = Mockito.mock(CompletionHandler.class);
        base64ToFileConverter.convert(message, "/testDir3/", "testFile1.jpeg", completionHandler);
        Mockito.verify(completionHandler).onSuccess("\\testDir3\\testFile1.jpeg");
    }
}